import React, { useState, useEffect } from "react";
import axios from "axios";
import { View, Text } from "react-native";

const CountryDetails = () => {
	const [confirmed, setConfirmed] = useState([]);
	const [recovered, setRecovered] = useState([]);
	const [deaths, setDeaths] = useState([]);

	useEffect(() => {
		const getConfirmed = () => {
			axios
				.get(`https://api.covid19api.com/total/country/cuba/status/confirmed`)
				.then(res =>
					setConfirmed(
						res.data.reduce((acc, country) => (acc += country.Cases), 0)
					)
				)
				.catch(error => console.log(error));
		};
		const getRecovered = () => {
			axios
				.get(`https://api.covid19api.com/total/country/cuba/status/recovered`)
				.then(res =>
					setConfirmed(
						res.data.reduce((acc, country) => (acc += country.Cases), 0)
					)
				)
				.catch(error => console.log(error));
		};
		const getDeaths = () => {
			axios
				.get(`https://api.covid19api.com/total/country/cuba/status/deaths`)
				.then(res =>
					setConfirmed(
						res.data.reduce((acc, country) => (acc += country.Cases), 0)
					)
				)
				.catch(error => console.log(error));
		};
	}, []);

	return (
		<View>
			<Text>Confirmed: {confirmed}</Text>
			<Text>Recovered: {recovered}</Text>
			<Text>Deaths: {deaths}</Text>
		</View>
	);
};

export default CountryDetails;
